import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ClasseService } from 'src/app/classe.service';
import { LoadingService } from 'src/app/loading.service';
import { IClassroom, IMatiere } from '../user';

@Component({
  selector: 'app-matiere',
  templateUrl: './matiere.component.html',
  styleUrls: ['./matiere.component.css']
})
export class MatiereComponent implements OnInit {
  name:String='';
  note:String='';
  //classeroom:String=""
  Classes:IClassroom[]=[]
  Matieres:IMatiere[]=[]
  constructor(private Cla:ClasseService,private mta:LoadingService,private router:Router){}
  ngOnInit(): void {
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      //console.log(this.Classes);

      this.fetchMatiereDetailsForClassrooms();

    });



  }

  fetchMatiereDetailsForClassrooms(){
    this.Classes.forEach((classroom) => {
      //classroom.matieres = [];
      //console.log(classroom.matieres+ 'classeroom');

      // Loop through the matieres IDs in the classroom
      classroom.matieres.forEach((matiereId) => {
        //console.log(matiereId);

        // Make an API call to fetch matiere details by ID
        this.mta.getMatiere(matiereId).subscribe((matiereDetails) => {
          // Push the fetched matiere details to the classroom's classroomMatieres array
          classroom.matieres.push(matiereDetails);
          this.Matieres=classroom

        });

      });
      console.log(classroom);
    });

  }

}
