import { Component, OnInit } from '@angular/core';
import { filter, tap } from 'rxjs';
import { UserServiceService } from 'src/app/user-service.service';

@Component({
  selector: 'app-add-cla',
  templateUrl: './add-cla.component.html',
  styleUrls: ['./add-cla.component.css']
})
export class AddClaComponent implements OnInit {

  ngOnInit(): void {

    this.teacher.getTeacher().subscribe(
      data => {
        // Assign the data to the students array
        this.students = data.filter((user:any) => user.role === 'student');

        // Log the data to the console
        console.log('Filtered Student Data:', this.students);
      },
      error => {
        console.error(error);
      }
    );

  }
  constructor(private teacher:UserServiceService){

  }
  public students:any[]=[]

}
