import { Component, OnInit } from '@angular/core';
import { filter, tap } from 'rxjs';
import { UserServiceService } from 'src/app/user-service.service';

@Component({
  selector: 'app-add-cla',
  templateUrl: './add-cla.component.html',
  styleUrls: ['./add-cla.component.css']
})
export class AddClaComponent implements OnInit {

  ngOnInit(): void {

    this.showteacher();
    //console.log(this.data);


  }
  constructor(private teacher:UserServiceService){}
  data:any[]=[]
  showteacher(){
    this.teacher.getTeacher().pipe(
      filter(student=>student.role==='student')
    ).subscribe(filteredUser => this.data=filteredUser );


  }
}

<div class="flex flex-col  items-center justify-center gap-4 ">
    <input type="text"   class=" border w-2/6 border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-300 focus:border-blue-300 block  p-2.5 cursor-pointer" placeholder="Nom de Classe"/>
    <select id="countries" *ngFor="let item of data" class="border w-2/6 border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-300 focus:border-blue-300 block  p-2.5 cursor-pointer">
        <option selected>classe teacher</option>
        <option value="US">United States</option>
        <option value="CA">Canada</option>
        <option value="FR">France</option>
        <option value="DE">Germany</option>
    </select>
    <button type="button"  class="text-white w-2/6 bg-[#3b5998] hover:bg-[#3b5998]/90 focus:ring-4 focus:outline-none focus:ring-[#3b5998]/50 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center mr-2 mb-2">
        <i class="bi bi-person-plus w-6 h-6 mr-2"></i>
        Submit
      </button>


</div>
