import { Component, OnInit } from '@angular/core';
import { ClasseService } from 'src/app/classe.service';
import { LoadingService } from 'src/app/loading.service';

@Component({
  selector: 'app-matiere',
  templateUrl: './matiere.component.html',
  styleUrls: ['./matiere.component.css']
})
export class MatiereComponent implements OnInit {
  name:String='';
  note:String='';
  classeroom:String=""
  Classes:any[]=[]
  constructor(private Cla:ClasseService,private mta:LoadingService,private router:Router){}
  ngOnInit(): void {
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      console.log(this.Classes);

    });
  }
  ajetudient(){


    this.mta.ajMatiere(this.name,this.note).subscribe(
      (response) => {
        const studentId = response.body.userId // Access the generated student ID from the response
        console.log('Student ID:', studentId);
        // Now you have the generated student ID

        this.Cla.updateClasseroom(this.classeroom, studentId).subscribe(
          (updatedClassroom) => {
            console.log('Classroom updated successfully:', updatedClassroom);
          },
          (error) => {
            console.error('Error:', error);
            // Handle error
          }
        );
      },
      (error) => {
        console.error('Error:', error);
        // Handle error
      }
    );
      this.router.navigate(['/matiere']);
    }


}
