import { Component, OnInit } from '@angular/core';
import { ClasseService } from 'src/app/classe.service';
import { IClassroom, IUser } from '../user';


@Component({
  selector: 'app-attendance',
  templateUrl: './attendance.component.html',
  styleUrls: ['./attendance.component.css']
})
export class AttendanceComponent implements OnInit {
  today: Date = new Date();
  cla:String='Classe A'
  selectedStatus: { [studentId: string]: string } = {}
  Classes: IClassroom[] = [];
  //Students:IClassroom[]=[]
  filteredList: IClassroom[] = [];
  constructor(private Cla:ClasseService,private att:Atte){}

  ngOnInit(): void {

    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe;

      this.updateFilteredList()
    });

  }
  updateFilteredList() {
    this.filteredList = this.Classes.filter((classroom) => classroom.name === this.cla);
    console.log(this.filteredList);
  }

  onSubmit(){
    console.log(this.selectedStatus);
  }

}
