import { Component, OnInit } from '@angular/core';
import { ClasseService } from 'src/app/classe.service';
import { IClassroom, IUser } from '../user';
import { AttendanceService } from 'src/app/attendance.service';


@Component({
  selector: 'app-attendance',
  templateUrl: './attendance.component.html',
  styleUrls: ['./attendance.component.css']
})
export class AttendanceComponent implements OnInit {
  today: Date = new Date();
  cla:String='Classe A'
  selectedStatus: { [studentId: string]: string } = {}
  Classes: IClassroom[] = [];
  //Students:IClassroom[]=[]
  filteredList: IClassroom[] = [];
  constructor(private Cla:ClasseService,private att:AttendanceService){}

  ngOnInit(): void {

    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe;

      this.updateFilteredList()
    });

  }
  updateFilteredList() {
    this.filteredList = this.Classes.filter((classroom) => classroom.name === this.cla);
    console.log(this.filteredList);
  }

  onSubmit(studentId: string) {
    const selectedStatus = this.selectedStatus[studentId];

    if (studentId && selectedStatus) {
      this.att.createAttendance({ iduser: studentId, status: selectedStatus }).subscribe(response => {
        // Handle the response from the API if needed
        console.log('API response:', response);
      });
    } else {
      console.error('Both studentId and status must be provided.');
    }
  }
}
