import { Injectable } from '@angular/core';
import { IUser } from './pages/user';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class UserServiceService {
  private readonly url="http://localhost:9000/api/users";
  constructor(private http:HttpClient) { }

  public getStudent():Observable<IUser[]>
  {
    return this.http.get<IUser[]>(this.url).pipe(
        tap(users=>console.log("users",users)),
        catchError(this.handleError)
    );
  }

  public ajStudent(user:{name: string, register:string, genre:string, photo:string,dateN:Date,adresse:string,role:string,father:string,profession:string,nationalid:string,education:string,mobile:string,email:string}):Observable<any>{
    return this.http.post(this.url,user).pipe(
      catchError(this.handleError)
    )
  }

  private handleError(error: HttpErrorResponse) {
    if (error.status === 0) {
      // A client-side or network error occurred. Handle it accordingly.
      console.error('An error occurred:', error.error);
    } else {
      // The backend returned an unsuccessful response code.
      // The response body may contain clues as to what went wrong.
      console.error(
        `Backend returned code ${error.status}, body was: `, error.error);
    }
    // Return an observable with a user-facing error message.
    return throwError(() => new Error('Something bad happened; please try again later.'));
  }
}
