import { Component } from '@angular/core';
import { IUser } from 'src/models';
import { ClasseroomService } from '../classeroom.service';
import { UserService } from '../user.service';

@Component({
  selector: 'app-classeroom',
  templateUrl: './classeroom.component.html',
  styleUrls: ['./classeroom.component.css']
})
export class ClasseroomComponent {
  receivedId=''
  Classes: any[] = [];
  Students:IUser [] = [];
  constructor(private student:UserService,private Cla:ClasseroomService){}
  ngOnInit(): void {
    this.student.getAll()
    .subscribe((data: IUser[]) => {
      // Filter users by role 'student'
      this.Students = data.filter(user => user.role === 'Student');
      console.log(this.Students);

    });
    this.Cla.classes$.subscribe((classes) => {
      this.Classes = classes;
    });
    console.log(this.Classes);

    this.fetchClasses();
  }

  fetchClasses() {
    this.Cla.getClasse().subscribe(
      (classes) => {
        // This will update the Classes array when classes are fetched
        this.Classes = classes;
        console.log(this.Classes);
      },
      (error) => {
        // Handle errors here
        console.error('Error fetching classes:', error);
      }
    );
  }

  onItemClick(itemId: string) {
    this.receivedId = itemId;
  }

  deleteClasse(userId: string){
    this.Cla.deleteClasse(userId).subscribe(
      () => {
        // Handle the success of the deletion here
        console.log(`Class with ID ${userId} deleted successfully.`);
        this.fetchClasses();
      },
      (error) => {
        // Handle any errors that may occur during deletion
        console.error(`Error deleting class with ID ${userId}:`, error);
      }
    );
  }
}
