import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  message=""
  isLoading=false
  email='';
  pass='';
  constructor(private router: Router,private user:UserService) {}
 login() {
    this.user.login(this.email, this.pass)
      .subscribe((response) => {
        console.log(response);

        if (response.role === 'Professeur') {
          // If the role is 'employee', navigate to the employee profile
          // Store the email in local storage
          localStorage.setItem('userId', response.id);
          //this.user.setUserId(response.id)
          this.isLoading = true;

          this.router.navigate(['/dashboard/profile', response.id, 'Professeur']);



        } else if (response.role === 'Student') {
          localStorage.setItem('userId', response.id);


          setTimeout(() => {
    // After 5 seconds, set isLoading back to false

         this.router.navigate(['/dashboard/profile', response.id, 'student']);
        }, 3000);


        } else if (response.role === 'personnel de direction') {
          this.isLoading = true;
        localStorage.setItem('userId', response.id);
        setTimeout(() => {
          this.message='welcome! Mr'+response.name
          this.router.navigate(['/dashboard/home']);;
              }, 2000);

        } else {
          // Handle other roles or scenarios
          console.log('Unknown role:', response.id);
        }
     });
  }
}
