import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IAttendance, Ipayment } from 'src/models';
import { UserService } from '../user.service';
import { PaymentService } from '../payment.service';
import { AttendanceService } from '../attendance.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{
  attonePresent: IAttendance[] = [];
  attoneAbsent: IAttendance[] = [];
  attoneLate: IAttendance[] = [];
  attendancePercentageAllMonths: number = 0;
  attendancePercentageCurrentMonth: number = 0;
  attendancePercentageAllMonthsPresent: number = 0;
  attendancePercentageAllMonthsAbsent: number = 0;
  attendancePercentageAllMonthsLate: number = 0;
  attendancePercentageCurrentMonthPresent: number = 0;
  attendancePercentageCurrentMonthAbsent: number = 0;
  attendancePercentageCurrentMonthLate: number = 0;
  data: any = [];
  attone: IAttendance[] = [];
  id='';
  dataType: String='';
  payments:Ipayment[]=[];
  constructor(private route:ActivatedRoute,private user:UserService,private payment:PaymentService,private attendanceService: AttendanceService){}
  ngOnInit(): void {
    this.percentageattendance();
   // const id=this.route.snapshot.paramMap.get("id");
    this.route.params.subscribe(params => {
      this.dataType = params['type'];
      this.id=params['id'] // Will be 'student' or 'employee'
    });

    this.user.getUserById(this.id).subscribe({
      next:student => { this.data = student }
     })
     if (this.data==='Employee') {
      this.payment.getAllPayments().subscribe((payment: Ipayment[]) => {
        // Filter users by role 'employee'
        console.log(this.payments);
        this.payments = payment.filter(payment => payment.userId ===this.id)

        console.log(this.payments);

      });
     } else {
      this.payment.getAllPayments().subscribe((payment: Ipayment[]) => {
        // Filter users by role 'student'
        this.payments = payment.filter(payment => payment.userId ===this.id)

        console.log(this.payments);
      });
     }
  }

  percentageattendance() {
    // Fetch all attendance records
    this.attendanceService.getAllAttendance().subscribe(
      (att: IAttendance[]) => {
        // Handle the fetched attendance records here
        this.attone = att.filter(att => att.iduser._id === this.id);

        // Filter records for the current month
        const currentDate = new Date();
        const currentMonth = currentDate.getMonth();
        const currentYear = currentDate.getFullYear();
        this.attonePresent = this.attone.filter(atte => {
          const attDate = new Date(atte.datenow); // Use 'datenow' property for date filtering
          return attDate.getMonth() === currentMonth && attDate.getFullYear() === currentYear && atte.status === 'present';
        });

        this.attoneAbsent = this.attone.filter(atte => {
          const attDate = new Date(atte.datenow); // Use 'datenow' property for date filtering
          return attDate.getMonth() === currentMonth && attDate.getFullYear() === currentYear && atte.status === 'absent';
        });

        this.attoneLate = this.attone.filter(atte => {
          const attDate = new Date(atte.datenow); // Use 'datenow' property for date filtering
          return attDate.getMonth() === currentMonth && attDate.getFullYear() === currentYear && atte.status === 'late';
        });

        // Calculate attendance percentages
        const totalRecordsAllMonths = this.attone.length;
        const totalRecordsCurrentMonth = this.attonePresent.length + this.attoneAbsent.length + this.attoneLate.length;

        if (totalRecordsAllMonths > 0) {
          this.attendancePercentageAllMonthsPresent = (this.attonePresent.length / totalRecordsAllMonths) * 100;
          this.attendancePercentageAllMonthsAbsent = (this.attoneAbsent.length / totalRecordsAllMonths) * 100;
          this.attendancePercentageAllMonthsLate = (this.attoneLate.length / totalRecordsAllMonths) * 100;
        }

        if (totalRecordsCurrentMonth > 0) {
          this.attendancePercentageCurrentMonthPresent = (this.attonePresent.length / totalRecordsCurrentMonth) * 100;
          this.attendancePercentageCurrentMonthAbsent = (this.attoneAbsent.length / totalRecordsCurrentMonth) * 100;
          this.attendancePercentageCurrentMonthLate = (this.attoneLate.length / totalRecordsCurrentMonth) * 100;
        }

        console.log('Attendance Records for All Months:', this.attone);
        console.log('Attendance Records for Current Month (Present):', this.attonePresent);
