import { Component, OnInit } from '@angular/core';
import { ClasseroomService } from '../classeroom.service';
import { Router } from '@angular/router';
import { IClassroom } from 'src/models';
import { MatiereService } from '../matiere.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-matiere',
  templateUrl: './matiere.component.html',
  styleUrls: ['./matiere.component.css']
})
export class MatiereComponent implements OnInit{
  MatierForm: FormGroup= new FormGroup({});
  receivedId=''
  name:String='';
  note:String='';
  classeroom:String=""
  Classes:IClassroom[]=[]
  Matieres:String[]=[]
  constructor(private Cla:ClasseroomService,private formBuilder: FormBuilder,
    private router:Router,private mta:MatiereService){
      this.MatierForm = this.formBuilder.group({
        name:['', Validators.required],
        note:['', Validators.required]
      })
    }
  ngOnInit(): void {
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      console.log(this.Classes);
    });

  }
  ajMata() {
    if (this.MatierForm.valid) {
      const formValues = this.MatierForm.value;

      // Check if you have an existing matière ID (indicating an update)
      if (this.receivedId!==null) {
        // Perform update logic here
        this.mta.updateMatiere(this.receivedId, formValues).subscribe(
          (updatedMatiere) => {
            // Handle the update response, e.g., show a success message
            console.log('Matière updated:', updatedMatiere);
          },
          (error) => {
            console.error('Error updating matière:', error);
          }
        );
      } else {
        // Perform add logic here
        this.mta.ajMatiere(formValues).subscribe(
          (response) => {
            const matiereId = response.body.matiereId;

            this.Cla.updateClassroomAndAddMatiere(this.classeroom, matiereId).subscribe(
              (updatedClassroom) => {
                this.Classes.push(updatedClassroom);
                this.fetchdata();
                // Handle the add response, e.g., show a success message
                console.log('Matière added:', response);
              },
              (error) => {
                console.error('Error updating classroom:', error);
              }
            );
          },
          (error) => {
            console.error('Error creating matière:', error);
          }
        );
      }
    }
  }


  editUser() {
    this.mta.getUserById(this.receivedId).subscribe((user) => {
      console.log('User Data:', user);
      this.mta.patchValue(user);
      console.log('Form Data After Patching:', this.userForm.value);
    });
  }

  onItemClick(itemId: string) {
    this.receivedId = itemId;
  }
    onDeleteMatiere(id: string): void {
      this.mta.deleteMatiereById(id).subscribe(
        () => {
          // Handle success, e.g., refresh the list of Matieres
          console.log('Matiere deleted successfully');
          this.fetchdata()
        },
        (error) => {
          // Handle error, e.g., display an error message
          console.error('Error deleting Matiere:', error);
        }
      );
    }

    fetchdata(){
      this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      //console.log(this.Classes);
    });
    }
}
<div class="d-flex justify-content-between">
    <select class="form-select" aria-label="Default select example">
      <option selected>Open this select menu</option>
      <option value="1">One</option>
      <option value="2">Two</option>
      <option value="3">Three</option>
    </select>
    <button class="btn btn-info" data-toggle="modal" data-target="#exampleModal">
      <span class="btn-label">
          <i class="fas fa-user-plus"></i>
      </span>
      Matiere
  </button>
  </div>

<div class="container mt-4">
    <div class="row gx-5">
      <div class="col-6" *ngFor="let classroom of Classes">
<table class="table table-hover table-bordered table-head-bg-info table-bordered-bd-info">
    <thead>
        <tr>
            <th scope="col">#</th>
            <th scope="col">Nom de Classe </th>
            <th scope="col">Matiere</th>
            <th scope="col">Note</th>
            <th scope="col">Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let matiere of classroom.matieres; let i = index">
            <td>{{ i + 1 }}</td>
            <td>{{classroom.name}}</td>
            <td>{{matiere.name}}</td>
            <td>{{matiere.note}}</td>
            <td>
                <div class="d-flex justify-content-around">
                <i class="fas fa-pen text-secondary" data-toggle="modal" data-target="#exampleModal"></i>
                <i class="fas fa-trash text-danger"(click)="onItemClick(matiere._id)" data-toggle="modal" data-target="#modal-danger"></i>
            </div>
            </td>
        </tr>
    </tbody>
</table>
      </div>
    </div>
</div>


<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
	<div class="modal-dialog" role="document">
		<div class="modal-content">
			<div class="modal-header">
				<h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
				<button type="button" class="close" data-dismiss="modal" aria-label="Close">
					<span aria-hidden="true">&times;</span>
				</button>
			</div>
            <form [formGroup]="MatierForm" (ngSubmit)=" ajMata()">
			<div class="modal-body">

                    <div class="form-group">
                        <label for="exampleFormControlSelect1">Classe</label>
                        <select class="form-control" [(ngModel)]="classeroom" [ngModelOptions]="{standalone: true}" id="exampleFormControlSelect1">
                            <option *ngFor="let item of Classes" value="{{item._id}}">{{item.name}}</option>
                        </select>
                    </div>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Nom Matiere</label>
                        <input type="text" formControlName="name" class="form-control" id="name"  placeholder="name">
                    </div>
                    <div class="form-group">
                        <label for="exampleInputPassword1">Note</label>
                        <input type="number" formControlName="note" class="form-control" id="note" placeholder="Note" min="10" max="20">
                    </div>
			</div>
			<div class="modal-footer">
				<button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
				<button type="submit"  class="btn btn-primary" >Save changes</button>
			</div>
        </form>
		</div>
	</div>
</div>



  <!--Danger Modal Templates-->
