import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { IUser } from 'src/models';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ClasseroomService } from '../classeroom.service';
import { ActivatedRoute, Router } from '@angular/router';
@Component({
  selector: 'app-admission',
  templateUrl: './admission.component.html',
  styleUrls: ['./admission.component.css']
})
export class AdmissionComponent implements OnInit {
  dataType: String='';
  id=''
  users: IUser[]=[];
  userForm: FormGroup= new FormGroup({});
  Classes: any[] = [];
  constructor(private userService: UserService,
    private formBuilder: FormBuilder,
    private router:Router,
    private route: ActivatedRoute,
    private Cla:ClasseroomService) {
    this.userForm = this.formBuilder.group({
name: ['', Validators.required],
register: ['', Validators.required],
genre: ['', Validators.required],
dateN: ['', Validators.required],
adresse: ['', Validators.required],
role: ['Student',Validators.required],
father: ['', Validators.required],
profession: ['', Validators.required],
nationalId: ['', Validators.required],
education: ['', Validators.required],
mobile: ['', Validators.required],
email: ['', Validators.required],
password: ['', Validators.required],
salaire: ['', ],
classe: ['', ],


    });
  }
  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.dataType = params['type'];
      this.id=params['id'] // Will be 'student' or 'employee'
    });
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      console.log(this.Classes);

    });
  }
  addUser() {
    if (this.userForm.valid) {
      // The form is valid, you can access the form values using this.userForm.value
      const newUser = this.userForm.value;

      // Call the createUser method from your user service
      this.userService.createUser(newUser).subscribe((user) => {
        console.log('User added:', user);
        // Optionally, reset the form
        this.userForm.reset();
      });
    } else {
      // The form is invalid, so you can inspect the form controls for specific errors
      Object.keys(this.userForm.controls).forEach((key) => {
        const control = this.userForm.get(key);
        if (control && control.invalid) {
          console.log(`Form control "${key}" has the following errors:`, control.errors);
        }
      });
    }
  }



}
<form [formGroup]="userForm" (ngSubmit)="addUser()">

<div class="d-flex justify-content-between text-light py-2 px-4 bg-primary align-items-center ">
    <p class=''> Information {{dataType}}</p>
    <p class='required'>[ *Required]</p>
</div>

<div class="container">
<div class='row gx-5'>
    <div class="form-group col-4">
		<label for="exampleFormControlInput1">Nom Complete</label>
		<input type="text" class="form-control" id="name"  formControlName="name" placeholder="Nom Complete">
	</div>
    <div class="form-group col-4">
		<label for="exampleFormControlInput1">Code Etudient</label>
		<input  type="text" class="form-control" id="register"  formControlName="register" placeholder="register">
	</div>
<div class="form-group col-4" *ngIf="dataType === 'Student'">
    <label for="exampleFormControlSelect1">Choisire Classe</label>
    <select  class="form-control"id="classe">
        <option *ngFor="let item of Classes"  value="{{item._id}}">{{item.name}}</option>
    </select>
</div>

<div class="form-group col-4">
    <label for="exampleFormControlInput1">Date Naissance</label>
    <input type="date" class="form-control" id="dateN"  formControlName="dateN" >
</div>
<div class="form-group col-4">
    <label for="exampleFormControlSelect1">Type Utilisateur</label>
    <select class="form-control"id="role"  formControlName="role">
        <option selected value="Professeur">Professeur</option>
        <option value="personnel de direction">personnel de direction</option>
        <option value="gérant de magasin">gérant de magasin</option>
        <option value="comptable">comptable</option>
        <option *ngIf="dataType !== 'Student'" value="Student">Student</option>
    </select>
</div>
<div class="form-group col-4">
    <label for="exampleFormControlSelect1">Genre</label>
    <select  class="form-control" id="genre"  formControlName="genre">
        <option value="Masculin">Masculin</option>
        <option value="Feminin">Feminin</option>
    </select>
</div>
<div class="form-group col-4">
    <label for="exampleFormControlInput1">Adresse</label>
    <input  type="text" class="form-control" id="adresse"  formControlName="adresse" placeholder="">
</div>
<div class="form-group col-4">
    <label for="exampleFormControlInput1">Password</label>
    <input type="text" class="form-control" id="password"  formControlName="password" placeholder="">
</div>
<div class="form-group col-4" *ngIf="dataType !== 'student'" >
    <label for="exampleFormControlInput1">Salaire</label>
    <input   type="text" class="form-control" id="salaire"   placeholder="">
</div>
</div>
</div>
<div class='d-flex justify-content-between text-light py-2 px-4 bg-primary align-items-center '>
    <p class=''>Tuteur Legale Info</p>
    <p class='float-end'>[Optional]</p>
</div>
<div class="container">
<div class='row gap-x-5'>
    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Nom de Pere</label>
        <input   type="text" class="form-control" id="father"  formControlName="father"  placeholder="">
    </div>

    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Carte Identiter</label>
        <input  type="text" class="form-control"id="nationalId"  formControlName="nationalId" placeholder="">
    </div>

    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Profession</label>
        <input  type="text" class="form-control" id="profession"  formControlName="profession" placeholder="">
    </div>

    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Education</label>
        <input   type="text" class="form-control" id="education"  formControlName="education" placeholder="">
    </div>

    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Mobile</label>
        <input   type="text" class="form-control" id="mobile"  formControlName="mobile" placeholder="">
    </div>

    <div class="form-group col-4">
        <label for="exampleFormControlInput1">Email</label>
        <input   type="text" class="form-control" id="email"  formControlName="email" placeholder="">
    </div>
    <button type="submit" class="btn btn-primary"><i class="fas fa-user-plus mr-2"></i>Enregistrer</button>
</div>
</div>
</form>
