import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, catchError, tap, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class MatiereService {
  matieres: BehaviorSubject<any[]> = new BehaviorSubject<any[]>([]);
  matieres$: Observable<any[]> = this.matieres.asObservable();
  private readonly url="http://localhost:9000/api/matiere";
  constructor(private http:HttpClient) { }
  ajMatiere(name: String,note: String): Observable<any> {
    const studentData = {
      name,
      note,
    };

    // Return the full HTTP response with the created student data
    return this.http.post(this.url, studentData, { observe: 'response' }).pipe(

      tap((addedMatier) => {
        const currentClasses = this.matieres.getValue();
        currentClasses.push(addedMatier);
        this.matieres.next(currentClasses);
        //console.log(addedClass);
        //console.log(currentClasses);
        //console.log(this.classes);
      }),
      catchError(this.handleError)
    );
  }
  private handleError(error: HttpErrorResponse) {
    if (error.status === 0) {
      // A client-side or network error occurred. Handle it accordingly.
      console.error('An error occurred:', error.error);
    } else {
      // The backend returned an unsuccessful response code.
      // The response body may contain clues as to what went wrong.
      console.error(
        `Backend returned code ${error.status}, body was: `, error.error);
    }
    // Return an observable with a user-facing error message.
    return throwError(() => new Error('Something bad happened; please try again later.'));
  }
  public getMatiere(id:String):Observable<any>
  {
    return this.http.get('http://localhost:9000/api/matiere/'+id).pipe(
        //tap(matiere=>console.log("matiere",matiere)),
        catchError(this.handleError)
    );
  }

  deleteMatiereById(id: string): Observable<any> {
    const url = `http://localhost:9000/api/matiere/${id}`; // Replace with your API endpoint
    return this.http.delete(url);
  }
}
