import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  message=""
  isLoading=false
  email='';
  pass='';
  constructor(private router: Router,private user:UserService) {}
 login() {
    this.user.login(this.email, this.pass)
      .subscribe((response) => {
        console.log(response);

        if (response.role !== 'Student') {
          this.isLoading = true;
        localStorage.setItem('userId', response.id);
        this.message=response.message
        setTimeout(() => {

          this.router.navigate(['/dashboard/home']);;
              }, 2000);





        } else if (response.role === 'personnel de direction') {
          localStorage.setItem('userId', response.id);


          setTimeout(() => {
    // After 5 seconds, set isLoading back to false

         this.router.navigate(['/profile', response.id, 'student']);
        }, 3000);


        }  else {
          // Handle other roles or scenarios
          console.log('Unknown role:', response.id);
        }
     });
  }
}
