import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { IUser } from 'src/models';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css']
})
export class EmployeeComponent implements OnInit {
  constructor(private userService:UserService) {}
  employees: IUser[] = [];
  receivedId: String='';
  ngOnInit(): void {
    this.userService.getAll()

    .subscribe((data: IUser[]) => {
      // Filter users by role 'student'
      this.employees = data.filter(user => user.role !== 'Student');
      //console.log(this.employees);

    });
  }
  onItemClick(itemId: String) {
    this.receivedId = itemId;
  }
  deleteUser(userId: String): void {
    this.userService.deleteUser(userId)
      .subscribe(
        () => {
          console.log('User deleted successfully.');
          // Perform any other actions after successful deletion.

        },
        (error) => {
          console.error('Error deleting user:', error);
          // Handle error gracefully.
        }
      );
      const studentIndex = this.employees.findIndex((user) => user._id === userId);

    // Find the index of the user to delete in the filteredList array


    // If the user is found in both arrays, remove them
    if (studentIndex !== -1 ) {
      this.employees.splice(studentIndex, 1);


  }
  }
}
