import { Component } from '@angular/core';
import {ToastrService} from 'ngx-toastr'

@Component({
  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.css']
})
export class ToastComponent {
constructor(private toast:ToastrService){}

messages(){
  this.toastr.success('Hello', 'Success', {
    positionClass: 'toast-top-right', // Position in the top-right corner
    progressBar: true, // Show a progress bar
    timeOut: 3000, // Duration in milliseconds
    closeButton: true, // Show a close button
    tapToDismiss: true, // Close on click
    progressAnimation: 'increasing', // Progress bar animation
    progressAnimationSpeed: 200, // Progress bar animation speed
    toastClass: 'toast-success', // Custom CSS class for the toast
    titleClass: 'toast-success-title', // Custom CSS class for the toast title
    messageClass: 'toast-success-message', // Custom CSS class for the toast message
  });
}
}
