import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, catchError, tap, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ClasseroomService {
  classes: BehaviorSubject<any[]> = new BehaviorSubject<any[]>([]);
  classes$: Observable<any[]> = this.classes.asObservable();
  private readonly url="http://localhost:9000/api/classes";
  constructor(private http:HttpClient) { 

  }

  public getClasse():Observable<any>
  {
    return this.http.get(this.url).pipe(
      tap((classe:any) => {


      }),
      catchError(this.handleError)
    );
  }
  private handleError(error: HttpErrorResponse) {
    if (error.status === 0) {
      // A client-side or network error occurred. Handle it accordingly.
      console.error('An error occurred:', error.error);
    } else {
      // The backend returned an unsuccessful response code.
      // The response body may contain clues as to what went wrong.
      console.error(
        `Backend returned code ${error.status}, body was: `, error.error);
    }
    // Return an observable with a user-facing error message.
    return throwError(() => new Error('Something bad happened; please try again later.'));
  }

  deleteClasse(id: string): Observable<any> {
    // Make an HTTP DELETE request to delete the class by ID
    return this.http.delete(`${this.url}/${id}`).pipe(
      tap(() => {
        // Update the classes list by removing the deleted class
        const currentClasses = this.classes.getValue();
        const updatedClasses = currentClasses.filter((classe) => classe.id !== id);
        this.classes.next(updatedClasses);
      }),
      catchError(this.handleError)
    );
  }

}
