import { Component, OnInit } from '@angular/core';
import { IUser } from 'src/models';
import { UserService } from '../user.service';
import { PaymentService } from '../payment.service';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent implements OnInit {
  employees:IUser[]=[];
  today="";
  selectedOption:IUser|any;
  bonus:string="0"
  showMessage = false;
  successMessage = '';
  constructor(private userService:UserService,private payment:PaymentService) {

  }
  ngOnInit(): void {
    this.userService.getAll()

  .subscribe((data: IUser[]) => {
    // Filter users by role 'student'
    this.employees = data.filter(user => user.role !== 'Student');
    this.selectedOption=this.employees;

  });
  }
  addPayment(): void {
    const paymentData = {
      userId: this.selectedOption._id, // Replace with the user ID for whom the payment is being made
      amount: parseInt(this.selectedOption.salaire)+parseInt(this.bonus),  // Replace with the payment amount
      paymentDate: this.today,  // Replace with the payment date
      paymentType: 'regularPayment',  // Replace with the payment type (e.g., 'collectionFee' or 'regularPayment')
      // Add any other payment details as needed
    };
    console.log(this.today);
    this.payment.createPayment(paymentData).subscribe(
      (response) => {
        console.log('Payment added successfully:', response);
        // You can perform additional actions after a successful payment creation
      },
      (error) => {
        console.error('Error adding payment:', error);
        // Handle errors appropriately (e.g., show an error message to the user)
      }
    );
  }
  checkEmployeePaymentStatus(employeeId: string): void {
    this.payment.hasEmployeeBeenPaidThisMonth(employeeId,this.today).subscribe(
      (hasBeenPaid) => {
        if (hasBeenPaid) {
          // Employee has already been paid for this month, handle accordingly
          console.log('Employee has already been paid for this month.');
          console.log(this.today);
          this.successMessage='Employee has already been paid for this month',
          this.showSuccessMessage();
        } else {
          // Employee has not been paid for this month, proceed with adding a new payment
          this.addPayment();
          this.successMessage='Payment added successfully',
          this.showSuccessMessage();
        }
      },
      (error) => {
        console.error('Error checking payment status:', error);
        // Handle errors appropriately
      }
    );
  }
  showSuccessMessage() {
    //this.successMessage = 'Classe ajoutée avec succès';
    this.showMessage = true;

    // Hide the message after a certain time (e.g., 5 seconds)
    setTimeout(() => {
      this.showMessage = false;
      this.successMessage = '';
     // this.router.navigate(['/dashboard/classe']);
    }, 5000);

  }
}
