import { Component, OnInit } from '@angular/core';
import { IUser } from 'src/models';
import { ClasseroomService } from '../classeroom.service';
import { UserService } from '../user.service';
import { FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-classeroom',
  templateUrl: './classeroom.component.html',
  styleUrls: ['./classeroom.component.css']
})
export class ClasseroomComponent implements OnInit {
  ClasseForm
  receivedId=''
  Classes: any[] = [];
  Employees:IUser [] = [];
  constructor(private employee:UserService,private Cla:ClasseroomService,private formBuilder: FormBuilder,)
  {
    this.ClasseForm = this.formBuilder.group({
      name: ['', Validators.required],
      prof: ['', Validators.required],
    })
  }
  ngOnInit(): void {
    this.employee.getAll()
    .subscribe((data: IUser[]) => {
      // Filter users by role 'Professeur'
      this.Employees = data.filter(user => user.role === 'Professeur');
      console.log(this.Employees);

    });
    this.Cla.classes$.subscribe((classes) => {
      this.Classes = classes;
    });
    console.log(this.Classes);

    this.fetchClasses();
  }

  fetchClasses() {
    this.Cla.getClasse().subscribe(
      (classes) => {
        // This will update the Classes array when classes are fetched
        this.Classes = classes;
        console.log(this.Classes);
      },
      (error) => {
        // Handle errors here
        console.error('Error fetching classes:', error);
      }
    );
  }

  onItemClick(itemId: string) {
    this.receivedId = itemId;
  }

  deleteClasse(userId: string){
    this.Cla.deleteClasse(userId).subscribe(
      () => {
        // Handle the success of the deletion here
        console.log(`Class with ID ${userId} deleted successfully.`);
        this.fetchClasses();
      },
      (error) => {
        // Handle any errors that may occur during deletion
        console.error(`Error deleting class with ID ${userId}:`, error);
      }
    );
  }

  ajClasse(){
    this.Cla.ajClasse(this.ClasseForm).subscribe({
      next:()=>console.log("save classe"+this.name+this.prof), 
   });
}

<div class="d-flex justify-content-between">
    <select class="form-select" aria-label="Default select example">
        <option selected>Open this select menu</option>
        <option value="1">One</option>
        <option value="2">Two</option>
        <option value="3">Three</option>
      </select>
    <button class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
        <span class="btn-label">
            <i class="fa fa-archive"></i>
        </span>
        Ajouter Classe
    </button>
</div>
<div class="row row-cols-1 row-cols-lg-4 gap-4 my-4">
    <div *ngFor="let item of Classes" class="col-12 col-lg-3 mb-4">
        <div class="card h-70 rounded-xxl cursor-pointer bg-secondary">
            <div class="card-body">
                <h5 class="card-title text-xl text-white pl-1 pt-2">{{item.name}}</h5>
                <div class="d-flex justify-content-between text-white pt-2">
                    <i class="fas fa-layer-group"></i>
                    <div class="d-flex row gx-5">
                        <a [routerLink]="['/dashboard/ajcla']" class="btn btn-sm text-primary rounded-circle bg-white">
                            <i class="fas fa-pen"></i>
                        </a>
                        <a (click)="onItemClick(item._id)" data-toggle="modal" data-target="#modal-danger" class="btn btn-sm text-danger bg-white rounded-circle">
                            <i class="fas fa-trash"></i>
                        </a>
                    </div>
                </div>
                <div class="d-flex justify-content-between pt-2">
                    <p class="text-white text-md">Student</p>
                    <p class="text-md text-white">{{item.students.length}}</p>
                </div>
            </div>
        </div>
    </div>
</div>


<!--Danger Modal Templates-->
<div id="modal-danger" class="modal modal-message modal-danger fade" style="display: none;" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h2 class="modal-title">Supprimer</h2>
            </div>
            <div class="modal-title"><i class="glyphicon fas fa-exclamation-triangle text-danger"></i></div>

            <div class="modal-body">Êtes-vous sûr de vouloir supprimer ce Classe ?</div>
            <div class="modal-footer">
                <button type="button" class="btn btn-danger" (click)="deleteClasse(receivedId)"  data-dismiss="modal">OK</button>
                <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
            </div>
        </div> <!-- / .modal-content -->
    </div> <!-- / .modal-dialog -->
</div>
<!--End Danger Modal Templates-->


<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
	<div class="modal-dialog" role="document">
		<div class="modal-content">
			<div class="modal-header">
				<h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
				<button type="button" class="close" data-dismiss="modal" aria-label="Close">
					<span aria-hidden="true">&times;</span>
				</button>
			</div>
            <form >
			<div class="modal-body">
                <div class="form-group">
                    <label for="exampleInputEmail1">Nom Classe</label>
                    <input type="text" formControlName="name" class="form-control" id="name"  placeholder="name">
                </div>
                    <div class="form-group">
                        <label for="exampleFormControlSelect1">Professeur</label>
                        <select class="form-control"  id="exampleFormControlSelect1">
                            <option *ngFor="let item of Employees" value="{{item._id}}">{{item.name}}</option>
                        </select>
                    </div>


			</div>
			<div class="modal-footer">
				<button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
				<button type="submit"  class="btn btn-primary" >Save changes</button>
			</div>
        </form>
		</div>
	</div>
</div>
