import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IClassroom, IUser } from 'src/models';
import { UserService } from '../user.service';
import { PaymentService } from '../payment.service';
import { ClasseroomService } from '../classeroom.service';

@Component({
  selector: 'app-collection',
  templateUrl: './collection.component.html',
  styleUrls: ['./collection.component.css']
})
export class CollectionComponent implements OnInit {
  today:string='';
  //data: any = [];
  mens="1500";
  dts="";
  insc='';
  trsp='';
  uni='';
  art='';
  dataType=''
  total:number=0
  Classes:IClassroom[]=[];
  Students:IUser [] = [];
  selectedClassroom: string | null = null;
  cc: String | null = null;
  selectedStudent: IUser | null = null;
  showMessage = false;
  successMessage = '';

  constructor(private route:ActivatedRoute,private user:UserService,private Cla:ClasseroomService,private payment:PaymentService){}
    ngOnInit(): void {
      this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes= classe;
    });
    this.loadStudents();
    }
    loadStudents(): void {
      // Get the selected classroom based on its _id
      const selectedClassroom = this.Classes.find(
        (classroom) => classroom._id === this.selectedClassroom

      );

      // Populate the students array with students from the selected classroom
      if (selectedClassroom) {
        this.Students = selectedClassroom.students;
        this.cc=selectedClassroom.name

      } else {
        this.Students = [];
      }
    }
    totalTTc(){
      this.total= parseInt(this.mens)+parseInt(this.insc)+parseInt(this.trsp)+parseInt(this.uni)+parseInt(this.art)
    }
    getSelectedStudent(): void {
      // Find the selected student in the Students array based on some criteria
      const selectedStudent = this.Students.find(
        (student) => student._id === this.dataType
      );

      // Check if the selected student was found
      if (selectedStudent) {
        // Assign the selected student to the selectedStudent variable
        this.selectedStudent = selectedStudent;
      } else {
        // If the student was not found, you can handle this case as needed
        this.selectedStudent = null;
        // Optionally, you can show an error message or take other actions.
      }
    }
    addPayment(): void {
      const paymentData = {
        userId: this.dataType, // Replace with the user ID for whom the payment is being made
        amount: parseInt(this.mens)+parseInt(this.insc)+parseInt(this.trsp)+parseInt(this.uni)+parseInt(this.art),  // Replace with the payment amount
        paymentDate: this.today,  // Replace with the payment date
        paymentType: 'collectionFee',
          // Replace with the payment type (e.g., 'collectionFee' or 'regularPayment')
        // Add any other payment details as needed


      };

      this.payment.createPayment(paymentData).subscribe(
        (response) => {
          console.log('Payment added successfully:', response);
          // You can perform additional actions after a successful payment creation
          console.log(this.today);
        },
        (error) => {
          console.error('Error adding payment:', error);
          // Handle errors appropriately (e.g., show an error message to the user)
        }
      );
    }
    checkEmployeePaymentStatus(): void {
      this.payment.hasEmployeeBeenPaidThisMonth(this.dataType,this.today).subscribe(
        (hasBeenPaid) => {
          if (hasBeenPaid) {
            // Employee has already been paid for this month, handle accordingly
           // console.log('Employee has already been paid for this month.');
            this.successMessage='Student has already paid for this month'+this.today,
            this.showSuccessMessage();
          } else {
            // Employee has not been paid for this month, proceed with adding a new payment
            this.addPayment();
            this.successMessage='Payment added successfully',
            this.showSuccessMessage();
          }
        },
        (error) => {
          console.error('Error checking payment status:', error);
          // Handle errors appropriately
        }
      );
    }
    showSuccessMessage() {
      //this.successMessage = 'Classe ajoutée avec succès';
      this.showMessage = true;

      // Hide the message after a certain time (e.g., 5 seconds)
      setTimeout(() => {
        this.showMessage = false;
        this.successMessage = '';
       // this.router.navigate(['/dashboard/classe']);
      }, 5000);

    }
}
