import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { IUser } from 'src/models';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ClasseroomService } from '../classeroom.service';
import { ActivatedRoute, Router } from '@angular/router';
@Component({
  selector: 'app-admission',
  templateUrl: './admission.component.html',
  styleUrls: ['./admission.component.css']
})
export class AdmissionComponent implements OnInit {
  dataType: String='';
  id=''
  users: IUser[]=[];
  userForm: FormGroup= new FormGroup({});
  Classes: any[] = [];
  constructor(private userService: UserService,
    private formBuilder: FormBuilder,
    private router:Router,
    private route: ActivatedRoute,
    private Cla:ClasseroomService) {
    this.userForm = this.formBuilder.group({
name: ['', Validators.required],
register: ['', Validators.required],
genre: ['', Validators.required],
dateN: ['', Validators.required],
adresse: ['', Validators.required],
role: ['Student',Validators.required],
father: ['', Validators.required],
profession: ['', Validators.required],
nationalId: ['', Validators.required],
education: ['', Validators.required],
mobile: ['', Validators.required],
email: ['', Validators.required],
password: ['', Validators.required],


    });
  }
  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.dataType = params['type'];
      this.id=params['id'] // Will be 'student' or 'employee'
    });
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      console.log(this.Classes);

    });
  }
  addUser() {
    if (this.userForm.valid) {
      // The form is valid, you can access the form values using this.userForm.value
      const newUser = this.userForm.value;

      // Call the createUser method from your user service
      this.userService.createUser(newUser).subscribe((user) => {
        console.log('User added:', user);
        // Optionally, reset the form
        this.userForm.reset();
      });
    } else {
      // The form is invalid, so you can inspect the form controls for specific errors
      Object.keys(this.userForm.controls).forEach((key) => {
        const control = this.userForm.get(key);
        if (control && control.invalid) {
          console.log(`Form control "${key}" has the following errors:`, control.errors);
        }
      });
    }
  }



}
