import { Component, OnInit } from '@angular/core';
import { IAttendance, IUser } from 'src/models';
import { UserService } from '../user.service';
import { AttendanceService } from '../attendance.service';
import { ClasseroomService } from '../classeroom.service';
import { PaymentService } from '../payment.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  thisMonthFees: number = 0;
  totalFees: number = 0;
  feeCollectionPercentage: number = 0;
  totalAmounts = {
    regular: 0,
    collection: 0,

  };
  profite=0
  paymentData: any[] = [];
  totalRegularPayments: number[] = [];
  totalCollectionFees: number[] = [];
  absentPercentage: number = 0;
  precencePercentage: number = 0;
  amount=0
  ttcEtudient=0
  students: IUser[] = [];
  Teachers: any[] = [];
  attemp:IAttendance[]=[];
  attstu:IAttendance[]=[];

  collectionPercentage: number =0
  constructor(private userService:UserService,private att:AttendanceService,private Cla:ClasseroomService,
     private paie:PaymentService){}
  ngOnInit(): void {
    this.getStudent();
    this.getProf();
    this.getattEmp();
    this.getattStu();
    this.calculateAbsentPercentage();
    this.calculatepresentPercentage();
    this.feepercent();

  }
  getStudent(){
    this.paie.getAllPayments().subscribe(data => {
      this.paymentData = data;
      this.paymentData.forEach((payment) => {

        const paymentType = payment.paymentType;
        const amount = payment.amount;

        // Check the payment type and accumulate the amount accordingly
        if (paymentType === 'regularPayment') {
          this.totalAmounts.regular += amount;
        } else if (paymentType === 'collectionFee') {
          this.totalAmounts.collection += amount;
        }
      });

      // Now, totalAmounts contains the total amount for "regular" and "collection" payment types
      //console.log(this.totalAmounts);
      //console.log(this.totalAmounts.collection-this.totalAmounts.regular);
      this.profite=this.totalAmounts.collection-this.totalAmounts.regular
      this.amount=this.totalAmounts.collection


    });
    this.userService.getAll()
  .subscribe((data: IUser[]) => {
    // Filter users by role 'student'
    this.students = data.filter(user => user.role === 'Student');

  });
  }

  getProf(){
    this.userService.getAll()
    .subscribe((data: IUser[]) => {
      // Filter users by role 'student'
      this.Teachers = data.filter(user => user.role !== 'Student');
      //console.log("all teachers"+JSON.stringify(this.Teachers));


    });
  }

  getattEmp() {
    const currentDate = new Date(); // Get the current date

    this.att.getAllAttendance()
      .subscribe((data: IAttendance[]) => {
        const uniqueNames = new Set<string>();

        // Filter users by status 'Present' and date matching the current date
        this.attemp = data
          .filter(user => user.status === 'Present')
          .filter(user => user.iduser.role !== 'Student')
          .filter(user => {
            // Convert the date from the data to a JavaScript Date object
            const attendanceDate = new Date(user.datenow);

            // Check if the date matches the current date
            if (
              attendanceDate.getDate() === currentDate.getDate() &&
              attendanceDate.getMonth() === currentDate.getMonth() &&
              attendanceDate.getFullYear() === currentDate.getFullYear()
            ) {
              // Check if the name is not already in the Set
              if (!uniqueNames.has(user.iduser.name)) {
                // Add the name to the Set
                uniqueNames.add(user.iduser.name);
                return true;
              }
            }
            return false;
          });

        console.log(this.attemp);

      });
  }

  getattStu() {
    const currentDate = new Date(); // Get the current date

    this.att.getAllAttendance()
      .subscribe((data: IAttendance[]) => {
        const uniqueNames = new Set<string>();

        // Filter users by status 'Absent', role 'Student', and date matching the current date
        this.attstu = data
          .filter(user => user.status === 'Absent')
          .filter(user => user.iduser.role === 'Student')
          .filter(user => {
            // Convert the date from the data to a JavaScript Date object
            const attendanceDate = new Date(user.datenow);

            // Check if the date matches the current date
            if (
              attendanceDate.getDate() === currentDate.getDate() &&
              attendanceDate.getMonth() === currentDate.getMonth() &&
              attendanceDate.getFullYear() === currentDate.getFullYear()
            ) {
              // Check if the name is not already in the Set
              if (!uniqueNames.has(user.iduser.name)) {
                // Add the name to the Set
                uniqueNames.add(user.iduser.name);
                return true;
              }
            }
            return false;
          });


      });
  }


  calculateAbsentPercentage() {
    //console.log("Total students:", this.students.length);
   // console.log("Absent students:", this.attstu.length);

    if (this.students.length > 0) {
     return this.absentPercentage = (this.attstu.length / this.students.length) * 100;
    } else {
      return this.absentPercentage = 0; // Handle cases where there are no students
    }

    //console.log("Absent percentage:", this.absentPercentage);
  }
  calculatepresentPercentage() {
    //console.log("Total employee:", this.Teachers.length);
    //console.log("Absent students:", this.attemp.length);

    if (this.students.length > 0) {
     return this.precencePercentage = (this.attemp.length / this.Teachers.length) * 100;
    } else {
      return this.precencePercentage = 0; // Handle cases where there are no students
    }

    //console.log("Absent percentage:", this.absentPercentage);
  }

  feepercent(){
    const currentDate = new Date();
    const currentMonth = currentDate.getMonth() + 1; // Adding 1 because getMonth() is zero-based
    const currentYear = currentDate.getFullYear();

    // Initialize variables for storing the total fees for this month and the total fees for all months


    this.paie.getAllPayments().subscribe(data => {
      this.paymentData = data;

      // Iterate through the payment data to calculate the fees
      this.paymentData.forEach((payment) => {
        const paymentDate = new Date(payment.paymentDate);
        console.log(paymentDate);

        const paymentMonth = paymentDate.getMonth() + 1;
        const paymentYear = paymentDate.getFullYear();

        // Check if the payment is for the current month and year
        if (paymentMonth === currentMonth && paymentYear === currentYear && payment.paymentType === 'collectionFee') {
          this.thisMonthFees += payment.amount;
        }

        // Accumulate all payments for the total fees
        this.totalFees += payment.amount;
      });

      // Calculate the fee collection percentage for this month
      this.feeCollectionPercentage = (this.thisMonthFees / this.totalFees) * 100;

      console.log(`Fee collection for this month: $${this.thisMonthFees}`);
      console.log(`Total fee collection: $${this.totalFees}`);
      console.log(`Fee collection percentage for this month: ${this.feeCollectionPercentage.toFixed(2)}%`);
    });

  }
}
