import { Component } from '@angular/core';

@Component({
  selector: 'app-bar-charts',
  templateUrl: './bar-charts.component.html',
  styleUrls: ['./bar-charts.component.css']
})
export class BarChartsComponent {
  paymentData: any[] = [];
  months: string[] = [];
  totalRegularPayments: number[] = [];
  totalCollectionFees: number[] = [];
  constructor(private Cla:ClasseroomService, private paie:PaymentService){}
  ngOnInit(): void {
    this.paie.getAllPayments().subscribe(data => {
      this.paymentData = data;

      // Extract unique months for labels
      this.months = this.extractMonths(data);

      // Calculate total regular payments and collection fees per month
      this.calculateTotalPaymentsPerMonth();
    });
  }

  extractMonths(data: Ipayment[]): string[] {
    const uniqueMonths = new Set<string>();
    data.forEach(item => {
      const paymentDate = new Date(item.paymentDate);
      const monthYear = `${paymentDate.getFullYear()}-${paymentDate.getMonth() + 1}`;
      uniqueMonths.add(monthYear);
    });
    return Array.from(uniqueMonths);
  }

  calculateTotalPaymentsPerMonth(): void {
    this.months.forEach(month => {
      const regularPaymentsForMonth = this.paymentData
        .filter(item => item.paymentType === 'regularPayment' && this.getMonthYear(item.paymentDate) === month)
        .reduce((total, item) => total + item.amount, 0);

      const collectionFeesForMonth = this.paymentData
        .filter(item => item.paymentType === 'collectionFee' && this.getMonthYear(item.paymentDate) === month)
        .reduce((total, item) => total + item.amount, 0);

      this.totalRegularPayments.push(regularPaymentsForMonth);
      this.totalCollectionFees.push(collectionFeesForMonth);
    });

    // Create and update the chart
    this.renderChart();
  }

  getMonthYear(date: string): string {
    const dateObject = new Date(date);
    const month = dateObject.getMonth() + 1;
    const year = dateObject.getFullYear();
    return `${year}-${month}`;
  }

  renderChart() {
    const ctx = document.getElementById('linechart') as HTMLCanvasElement;

    const mychart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: this.months,
        datasets: [
          {
            label: 'Total Regular Payments',
            data: this.totalRegularPayments,
            backgroundColor: 'rgba(0, 123, 255, 0.2)',
            borderColor: 'rgb(0, 123, 255)',
            borderWidth: 1
          },
          {
            label: 'Total Collection Fees',
            data: this.totalCollectionFees,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgb(255, 99, 132)',
            borderWidth: 1
          }
        ]
      },
      options: {
        responsive: true,
        scales: {
          x: {
            title: {
              display: true,
              text: 'Month'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Amount'
            }
          }
        }
      }
    });
  }
}
