import { Component } from '@angular/core';
import { ClasseroomService } from '../classeroom.service';
import { AttendanceService } from '../attendance.service';
import { UserService } from '../user.service';
import { IUser } from 'src/models';
import { Router } from '@angular/router';

@Component({
  selector: 'app-att-emp',
  templateUrl: './att-emp.component.html',
  styleUrls: ['./att-emp.component.css']
})
export class AttEmpComponent {
  today: Date = new Date();
  employees: any[] = []; 
  showMessage = false;
  successMessage = '';
  selectedStatus: { [employeeId: string]: string } = {};
  constructor(private att:AttendanceService,private userService:UserService,private router:Router){}
  ngOnInit(): void {
    this.userService.getAll()

  .subscribe((data: IUser[]) => {
    // Filter users by role 'student'
    this.employees = data.filter(user => user.role !== 'Student');
    console.log(this.employees);
    //this.filteredList=[...this.employees]
  });
  }
  onAdd() {
    // console.log('Before loop: selectedStatus', this.selectedStatus);

 for (const studentId of Object.keys(this.selectedStatus)) {

   const status = this.selectedStatus[studentId];

   // Now you can use studentId and status in your loop
   //console.log(`Student ID: ${studentId}, Status: ${status}`);

   // Check if both studentId and status are defined
   if (studentId && status) {
     // Create an attendance object based on the selected data
     const attendanceData = {
       iduser: studentId,
       status: status,
     };

     //console.log('Sending attendanceData', attendanceData);

     // Send the attendance data for this student
     this.att.createAttendance(attendanceData).subscribe(response => {
       // Handle the response from the API if needed
       console.log('API response:', response);
     });
   } 
 }

 this.showSuccessMessage();

   }

   showSuccessMessage() {
    this.successMessage = 'Attendance effectuer avec succès';
    this.showMessage = true;

    // Hide the message after a certain time (e.g., 5 seconds)
    setTimeout(() => {
      this.showMessage = false;
      this.successMessage = '';
      this.router.navigate(['/dashboard/home']);
    }, 5000);

  }
}
<div *ngIf="showMessage">
    <div class="alert alert-primary" role="alert">
        {{ successMessage }}
        </div>
    </div>
    <div class="d-flex justify-content-between">

        <div class="flex">
            <p class="title col"> Présence non encore marquée pour le {{today | date}}</p>
            <p class="subtitle col">Veuillez marquer  présence</p>
        </div>
        <div class="form-group">
            <label for="exampleFormControlInput1">Example select</label>
            <input type="date" [(ngModel)]="today"  class="form-control" id="exampleFormControlInput1">
        </div>
    </div>



          <div class="container">
            <table class="table table-hover table-bordered table-head-bg-info table-bordered-bd-info">
        <thead>
            <tr>
                <th scope="col">ID</th>
                <th scope="col">Photo </th>
                <th scope="col">Nom Complete </th>
                <th scope="col">PÈRE</th>

                <th scope="col">Actions</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let employee of employees">
                <td>{{ employee.register}}</td>
                <td><div class="avatar avatar-sm">
                    <img src="../assets/img/jm_denis.jpg" alt="..." class="avatar-img rounded-circle">
                </div></td>
                <td>{{employee.name}}</td>
                <td>{{employee.father}}</td>
                <td>
                    <div class="d-flex">
                        <div class="d-flex form-check mr-4">
                            <input [ngModelOptions]="{ standalone: true }"[(ngModel)]="selectedStatus[employee._id]" id="{{ 'radio-' + employee._id }}" type="radio" value="Present" name="{{ 'radio-group-' + employee._id }}" class="form-check-input"/>
                            <label for="{{ 'radio-' + employee._id }}" class="ml-2 form-check-label">P</label>
                        </div>
                        <div class="d-flex form-check mr-4">
                            <input [ngModelOptions]="{ standalone: true }" [(ngModel)]="selectedStatus[employee._id]" id="{{ 'radio-2-' + employee._id }}" type="radio" value="Late" name="{{ 'radio-group-' + employee._id }}" class="form-check-input"/>
                            <label for="{{ 'radio-2-' + employee._id }}" class="ml-2 form-check-label">L</label>
                        </div>
                        <div class="d-flex form-check mr-4">
                            <input [ngModelOptions]="{ standalone: true }"  [(ngModel)]="selectedStatus[employee._id]" id="{{ 'radio-3-' + employee._id }}" type="radio" value="Absent" name="{{ 'radio-group-' + employee._id }}" class="form-check-input"/>
                            <label for="{{ 'radio-3-' + employee._id }}" class="ml-2 form-check-label">A</label>
                        </div>
                        </div>  
                </td>
            </tr>
        </tbody>
    </table>
          </div>

    <div class="d-flex justify-center">
        <button type="button" (click)="onAdd()" class="btn btn-primary">
            Enregistrer
          </button>
    </div>
