import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PaymentService {
  private apiUrl = 'http://localhost:9000/api'; // Replace with your API URL

  constructor(private http: HttpClient) {}

  createPayment(paymentData: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/payments`, paymentData);
  }

  getAllPayments(): Observable<any> {
    return this.http.get(`${this.apiUrl}/payments`).pipe(
      tap(payment=>console.log("payment",payment)),
    );
  }

  getPaymentById(paymentId: string): Observable<any> {
    return this.http.get(`${this.apiUrl}/payments/${paymentId}`);
  }

  updatePayment(paymentId: string, paymentData: any): Observable<any> {
    return this.http.put(`${this.apiUrl}/payments/${paymentId}`, paymentData);
  }

  deletePayment(paymentId: string): Observable<any> {
    return this.http.delete(`${this.apiUrl}/payments/${paymentId}`);
  }
  hasEmployeeBeenPaidThisMonth(employeeId: string, inputDateStr: string): Observable<boolean> {
    const currentDate = new Date();
    const currentMonth = currentDate.getMonth() + 1; // Adding 1 because getMonth() is zero-based
    const currentYear = currentDate.getFullYear();
    try {
      // Parse the input date string into a Date object
      const inputDate = new Date(inputDateStr);

      // Get the month and year of the input date
      const inputMonth = inputDate.getMonth() + 1;
      const inputYear = inputDate.getFullYear();

      // Compare the current month and year with the input date's month and year
      const isPaidThisMonth = currentMonth === inputMonth && currentYear === inputYear;
        if (isPaidThisMonth) {
          const url = `${this.apiUrl}/payments/${employeeId}/checkPayment?month=${currentMonth}&year=${currentYear}`;
          console.log(isPaidThisMonth);
          return this.http.get<boolean>(url);
        } else {
          const url = `${this.apiUrl}/payments/${employeeId}/checkPayment?month=${inputMonth}&year=${inputYear}`;
          return this.http.get<boolean>(url);
        }
      // Define the URL to fetch payment records for the employee in the current month

  } catch (error) {
      console.error("Error parsing input date:", error);
      return of(false); // Return false in case of an error
  }
  }
}
