import { Component, OnInit } from '@angular/core';
import { ClasseroomService } from '../classeroom.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-matiere',
  templateUrl: './matiere.component.html',
  styleUrls: ['./matiere.component.css']
})
export class MatiereComponent implements OnInit{
  receivedId=''
  name:String='';
  note:String='';
  classeroom:String=""
  Classes:IClassroom[]=[]
  Matieres:String[]=[]
  constructor(private Cla:ClasseroomService,private router:Router,private mta:MatiereService){}
  ngOnInit(): void {
    this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      console.log(this.Classes);
    });

  }
  ajMata(){
    this.mta.ajMatiere(this.name,this.note).subscribe(
      (response) => {
        const matiereId = response.body.matiereId // Access the generated matiere ID from the response
        //console.log('matiere ID:', matiereId);
        //console.log('classeroom ID',this.classeroom);
        //this.Matieres.push(response)


        this.Cla.updateClassroomAndAddMatiere(this.classeroom, matiereId).subscribe(
          (updatedClassroom) => {
           // console.log('Classroom updated successfully:', updatedClassroom);
            this.Classes.push(updatedClassroom)
           // console.log(this.Classes.push(updatedClassroom));
           this.fetchdata()
          },
          (error) => {
            console.error('Error:', error);
            // Handle error
          }
        );
      },
      (error) => {
        console.error('Error:', error);
        // Handle error
      }
    );
  }
  onItemClick(itemId: string) {
    this.receivedId = itemId;
  }
    onDeleteMatiere(id: string): void {
      this.mta.deleteMatiereById(id).subscribe(
        () => {
          // Handle success, e.g., refresh the list of Matieres
          console.log('Matiere deleted successfully');
          this.fetchdata()
        },
        (error) => {
          // Handle error, e.g., display an error message
          console.error('Error deleting Matiere:', error);
        }
      );
    }

    fetchdata(){
      this.Cla.getClasse()
    .subscribe(classe =>{
      this.Classes = classe
      //console.log(this.Classes);
    });
    }
}
