import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { HttpClientModule } from '@angular/common/http';
import { AppRoutingModule } from './app-routing.module';
import { FormsModule } from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms';
import { AppComponent } from './app.component';
import { Routes ,RouterModule,} from '@angular/router';
import { LoginComponent } from './login/login.component';
import { SidebarComponent } from './sidebar/sidebar.component';
import { DashboardComponent } from './dashboard/dashboard.component';
import { ProfileComponent } from './profile/profile.component';
import { StudentComponent } from './student/student.component';
import { EmployeeComponent } from './employee/employee.component';
import { AdmissionComponent } from './admission/admission.component';
import { ClasseroomComponent } from './classeroom/classeroom.component';
import { MatiereComponent } from './matiere/matiere.component';
import { ExamComponent } from './exam/exam.component';
import { AttendanceComponent } from './attendance/attendance.component';
import { AttEmpComponent } from './att-emp/att-emp.component';


const routes: Routes = [
  { path: 'login', component: LoginComponent },
 // { path: 'error', component: ErrorPageComponent },
  {
    path: 'dashboard',
      component: SidebarComponent,
    //canActivate: [loginGuard],
    children: [
      { path: '', redirectTo: 'overview', pathMatch: 'full' }, // Default child route
      { path: 'home', component: DashboardComponent  },
      { path: 'profile/:id/:type', component: ProfileComponent },
     // { path: 'profile', component: ProfileComponent },
      { path: 'employee', component: EmployeeComponent },
      { path: 'student', component: StudentComponent },
      { path: 'classe', component: ClasseroomComponent },
      { path: 'admission/:type', component: AdmissionComponent },
      //{ path: 'admission', component: AdmissionComponent },
    //  { path: 'addcla', component: AjclasseComponent },
      { path: 'admission/:id/:type', component: AdmissionComponent },
    //  { path: 'addmta', component: AjmatiereComponent },
      { path: 'matiere', component: MatiereComponent },
      { path: 'att-stu', component: AttendanceComponent },
    //  { path: 'att-emp', component: AttEmpComponent },
    //  { path: 'salaire', component: SalaireComponent },
    //  { path: 'collection', component: CollectionComponent },
      //{ path: 'error', component: ErrorPageComponent },

    ],
  },
  { path: '', redirectTo: '/login', pathMatch: 'full' }, // Redirect to the login page by default
];
@NgModule({
  declarations: [
    AppComponent,
    LoginComponent,
    SidebarComponent,
    DashboardComponent,
    ProfileComponent,
    StudentComponent,
    EmployeeComponent,
    AdmissionComponent,
    ClasseroomComponent,
    MatiereComponent,
    ExamComponent,
    AttendanceComponent,
    AttEmpComponent
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    RouterModule.forRoot(routes),
    FormsModule,
    ReactiveFormsModule,
    HttpClientModule
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
